import { StyleSheet, Text, View, FlatList, TextInput, TouchableOpacity } from 'react-native'
import React, { useState } from 'react'
import { Background } from '@react-navigation/elements'
import axios from 'axios'



const ChatBot = () => {
  const [data, setdata] = useState([])
  const apiKey = 'sk - 3GedrgP9w0eaYkHIpTHtT3BlbkFJRAL2dUpi6KUxDTGb87OJ'
  const apiUrl = 'https://api.openai.com/v1/engines/davinci-codex/completions'
  const [textInput, setTextInput] = useState('')
  const handlesen = async () => {
    const prompt = textInput
    const response = await axios.post(apiUrl, {
      prompt: prompt,
      max_tokens: 1024,
      temprature: 0.5,
    }, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${apiKey}`
      }
    })
      ;
    const text = response.data.choices[0].text;
    setData([...data, { type: 'user', 'text': textInput }, { type: 'bot', 'text': text }]);
    setTextInput('');
  }


  return (
    <View styles={styles.container}>
      <Text styles={styles.title}>ChatBot</Text>
      <FlatList
        data={data}
        keyExtractor={(item, index) => index.toString()}
        style={styles.body}
        renderItem={({ item }) => (
          <View style={{ flexDirection: 'row', padding: 10 }}>
            <Text style={{ fontWeight: 'bold', color: item.type === 'user' ? 'green' : 'red' }}>{item.type === 'user' ? 'Ninza' : 'Bot'}</Text>
            <Text style={styles.bot}>{item.text}
            </Text>

          </View>
        )}
      />
      <TextInput
        style={styles.input}
        value={textInput}
        onChangeText={text => setTextInput(text)}
        placeholder="Ask me Anything"
      />
      <TouchableOpacity
        style={styles.button}
        onPress={handlesen}
      >
        <Text style={styles.buttonText}>Let's go</Text>
      </TouchableOpacity>
    </View>
  )
}


export default ChatBot

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
    alignItems: 'center'
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    marginBotton: 20,
    marginTop: 70
  },
  body: {
    backgroundColor: 'White',
    width: '102% ',
    margin: 10
  },
  bot: {
    fontSize: 16,

  },
  input: {
    borderWidth: 1,
    boderColor: 'black',
    borderRadius: 10,
    marginBotton: 10,
    height: 60,
    width: '90%'
  },
  button: {
    fontSize: 28,
    fontWeight: 'bold',
    marginBotton: 10,
    backgroundColor: 'yellow',
    width: '90%',
    height: 60,
    alignItems: 'center',
    justifyContent: 'center'
  },
  buttonText: {
    fontSize: 10,
    fontWeight: 'bold',
    color: 'blue'

  },


})
