import { StyleSheet, Text, View, SafeAreaView, Image, ScrollView } from "react-native";
import React, { useContext, useEffect } from "react";
import FitnessCards from "../components/FitnessCards";
import { FitnessItems } from "../Context";
import AsyncStorage from '@react-native-async-storage/async-storage';

const homescreena = () => {
  const {
    completed,
    setCompleted,
    workout,
    setWorkout,
    calories,
    setCalories,
    minutes,
    setMinutes,
  } = useContext(FitnessItems);

  useEffect(() => {
    const loadData = async () => {
      try {
        const [workoutData, caloriesData, minutesData] = await AsyncStorage.multiGet(['workout', 'calories', 'minutes']);
        setWorkout(parseInt(workoutData[1]) || 0);
        setCalories(parseInt(caloriesData[1]) || 0);
        setMinutes(parseInt(minutesData[1]) || 0);
      } catch (error) {
        console.error(error);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    const saveData = async () => {
      try {
        await AsyncStorage.multiSet([
          ['workout', workout.toString()],
          ['calories', calories.toString()],
          ['minutes', minutes.toString()]
        ]);
      } catch (error) {
        console.error(error);
      }
    };

    saveData();
  }, [workout, calories, minutes]);

  return (
    <ScrollView style={{ marginTop: 40 }}>
      <View
        style={{
          backgroundColor: "#CD853F",
          padding: 10,
          height: 200,
          width: "100%",
        }}
      >
        <Text style={{ color: "white", fontWeight: "bold", fontSize: 18 }}>
          HOME WORKOUT
        </Text>

        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "space-between",
            marginTop: 20,
          }}
        >
          <View>
            <Text
              style={{
                textAlign: "center",
                fontWeight: "bold",
                color: "white",
                fontSize: 18,
              }}
            >
              {workout}
            </Text>
            <Text style={{ color: "#D0D0D0", fontSize: 17, marginTop: 6 }}>
              WORKOUTS
            </Text>
          </View>

          <View>
            <Text
              style={{
                textAlign: "center",
                fontWeight: "bold",
                color: "white",
                fontSize: 18,
              }}
            >
              {calories}
            </Text>
            <Text style={{ color: "#D0D0D0", fontSize: 17, marginTop: 6 }}>
              KCAL
            </Text>
          </View>

          <View>
            <Text
              style={{
                textAlign: "center",
                fontWeight: "bold",
                color: "white",
                fontSize: 18,
              }}
            >
              {minutes}
            </Text>
            <Text style={{ color: "#D0D0D0", fontSize: 17, marginTop: 6 }}>
              MINS
            </Text>
          </View>
        </View>

        <View style={{ justifyContent: "center", alignItems: "center" }}>
          <Image
            style={{
              width: "90%",
              height: 120,
              marginTop: 20,
              borderRadius: 7,
            }}
            source={{
              uri: "https://cdn-images-1.medium.com/max/1200/1*yNz5U6LlH6vpNMd6vyKU6g.jpeg",
            }}
          />
        </View>
      </View>
      <FitnessCards />
    </ScrollView>
  );
};
export default homescreena;
