import React, { createContext, useEffect, useState } from "react";
import { AsyncStorage } from "react-native";

const FitnessItems = createContext();

const FitnessContext = ({ children }) => {
  const [completed, setCompleted] = useState([]);
  const [workout, setWorkout] = useState(0);
  const [calories, setCalories] = useState(0);
  const [minutes, setMinutes] = useState(0);

  useEffect(() => {
    // Load minutes and calories from local storage
    AsyncStorage.getItem("minutes").then((value) => {
      if (value !== null) {
        setMinutes(parseInt(value));
      }
    });
    AsyncStorage.getItem("calories").then((value) => {
      if (value !== null) {
        setCalories(parseInt(value));
      }
    });
  }, []);

  useEffect(() => {
    // Save minutes and calories to local storage when they change
    AsyncStorage.setItem("minutes", minutes.toString());
    AsyncStorage.setItem("calories", calories.toString());
  }, [minutes, calories]);

  return (
    <FitnessItems.Provider
      value={{
        completed,
        setCompleted,
        workout,
        setWorkout,
        calories,
        setCalories,
        minutes,
        setMinutes,
      }}
    >
      {children}
    </FitnessItems.Provider>
  );
};

export { FitnessContext, FitnessItems };
