import React, { useState, useEffect } from "react";
import { View, Text, StyleSheet, Pressable } from "react-native";
import { FitnessItems } from "../Context";
import AsyncStorage from "@react-native-async-storage/async-storage";

const homescreena = ({ navigation }) => {
  const [workout, setWorkout] = useState(0);
  const [completed, setCompleted] = useState([]);
  const [minutes, setMinutes] = useState(0);
  const [calories, setCalories] = useState(0);

  useEffect(() => {
    const loadState = async () => {
      try {
        const [workoutString, completedString, minutesString, caloriesString] = await AsyncStorage.multiGet(['workout', 'completed', 'minutes', 'calories']);
        const workoutValue = workoutString[1] || "0";
        const completedValue = JSON.parse(completedString[1] || "[]");
        const minutesValue = parseFloat(minutesString[1] || "0");
        const caloriesValue = parseFloat(caloriesString[1] || "0");

        setWorkout(parseInt(workoutValue));
        setCompleted(completedValue);
        setMinutes(minutesValue);
        setCalories(caloriesValue);
      } catch (e) {
        console.log(e);
      }
    };

    loadState();
  }, []);

  useEffect(() => {
    const saveState = async () => {
      try {
        await AsyncStorage.multiSet([
          ['workout', workout.toString()],
          ['completed', JSON.stringify(completed)],
          ['minutes', minutes.toString()],
          ['calories', calories.toString()]
        ]);
      } catch (e) {
        console.log(e);
      }
    };

    saveState();
  }, [workout, completed, minutes, calories]);

  return (
    <FitnessItems.Provider
      value={{
        completed,
        setCompleted,
        minutes,
        setMinutes,
        calories,
        setCalories,
        setWorkout,
        workout,
      }}
    >
      <View style={styles.container}>
        <Text style={styles.title}>Fitness Tracker</Text>
        <View style={styles.metricContainer}>
          <View style={styles.metric}>
            <Text style={styles.metricNumber}>{workout}</Text>
            <Text style={styles.metricText}>Workouts</Text>
          </View>
          <View style={styles.metric}>
            <Text style={styles.metricNumber}>{minutes.toFixed(1)}</Text>
            <Text style={styles.metricText}>Minutes</Text>
          </View>
          <View style={styles.metric}>
            <Text style={styles.metricNumber}>{calories.toFixed(1)}</Text>
            <Text style={styles.metricText}>Calories</Text>
          </View>
        </View>
        <Pressable
          style={styles.button}
          onPress={() => navigation.navigate("Workout")}
        >
          <Text style={styles.buttonText}>Start Workout</Text>
        </Pressable>
      </View>
    </FitnessItems.Provider>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontSize: 32,
    fontWeight: "bold",
    marginBottom: 20,
  },
  metricContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    marginBottom: 20,
  },
  metric: {
    alignItems: "center",
  },
  metricNumber: {
    fontSize: 28,
    fontWeight: "bold",
    marginBottom: 10,
  },
  metricText: {
    fontSize: 16,
    color: "#666",
  },
  button: {
    backgroundColor: "#0066cc",
    padding: 10,
    borderRadius: 5,
  },
  buttonText: {
    color: "#fff",
    fontSize: 18,
  },
});

export default homescreena
